const https = require('https')
const { URL } = require('url')
const url = require('url')
const streamReadAll = require('stream-read-all')

const reqOptions = url.parse('https://127.0.0.1:8000/three')
reqOptions.rejectUnauthorized = false
reqOptions.agent = new https.Agent()
const fs = require('fs')
reqOptions.ca = fs.readFile('/Users/lloydb/Documents/lwsjs/lws/ssl2/ca.crt')

https.get(reqOptions, async res => {
  const data = await streamReadAll(res)
  console.log(data.toString())
})
